package cn.henio;import io.vertx.core.AbstractVerticle;import io.vertx.core.http.HttpServerOptions;/** * created by mouzhanpeng at 2018/2/6 13:31 * * @since jdk 1.9.0 */public class FirstVerticle extends AbstractVerticle{    @Override    public void start() throws Exception {        vertx.createHttpServer(new HttpServerOptions().setLogActivity(true)).requestHandler(req -> req.response().end(FirstVerticle.this + "Hello World!")).listen(8080);        vertx.setPeriodic(1000, (l) -> {            vertx.createHttpClient().getNow(8080, "localhost", "/", resp -> {                resp.bodyHandler(body -> {                    System.out.println(body.toString("ISO-8859-1"));                });            });        });    }}