package cn.henio;import io.vertx.core.DeploymentOptions;import io.vertx.core.Vertx;import io.vertx.ext.unit.Async;import io.vertx.ext.unit.TestContext;import io.vertx.ext.unit.junit.VertxUnitRunner;import org.junit.After;import org.junit.Before;import org.junit.Test;import org.junit.runner.RunWith;/** * created by mouzhanpeng at 2018/2/6 13:31 * * @since jdk 1.9.0 */@RunWith(VertxUnitRunner.class)public class FisrtVerticleTest {    private Vertx vertx;    @Before    public void setUp(TestContext context) {        vertx = Vertx.vertx();        DeploymentOptions options = new DeploymentOptions().setInstances(16);        vertx.deployVerticle(FirstVerticle.class.getName(), options, context.asyncAssertSuccess(System.out::println));    }    @After    public void tearDown(TestContext context) {        vertx.close();    }    @Test    public void testApplication(TestContext context) {        final Async async = context.async();        vertx.createHttpClient().getNow(8888, "localhost", "/view/test", response -> {            response.handler(body -> {                System.err.println(body.toString());                context.assertTrue(true);                async.complete();            });        });    }}